/**
 * Convert a time string into a date object.
 * @param time hh:mm[am/pm] time string.
 */
function timeToDate(time) {
    let regex = /(\d{1,2}):(\d{1,2})(am|pm)/;
    let match = regex.exec(time);
    let hour = parseInt(match[1]);
    if (match[3] === 'pm' && hour !== 12) {
        hour += 12;
    }
    if (match[3] === 'am' && hour === 12) {
        hour = 0;
    }
    let minute = parseInt(match[2]);
    let date = new Date();
    date.setHours(hour);
    date.setMinutes(minute);
    date.setSeconds(0);
    date.setMilliseconds(0);
    return date;
}
/**
 * Convert theatre information into a list of showtimes.
 * @param theatre Unique theatre name.
 * @param theatreInfo Information about movie showtimes at a given theatre.
 */
function getShowtimes(theatre, theatreInfo) {
    let showtimes = [];
    for (let movie of theatreInfo) {
        for (let showtime of movie.times) {
            showtimes.push({
                title: movie.title,
                movieLink: movie.link,
                showtime: showtime.time,
                showtimeLink: showtime.link,
                theatre: theatre.name,
                theatreLink: theatre.link,
                sortTime: timeToDate(showtime.time).getTime()
            });
        }
    }
    return showtimes;
}
/**
 * Load a row into the movie table representing the showtime.
 * @param showtime Showtime information.
 */
function loadShowtime(showtime) {
    let tr = document.createElement('tr');
    let td = document.createElement('td');
    let time = document.createElement('a');
    time.setAttribute('href', showtime.showtimeLink);
    time.innerText = showtime.showtime;
    td.appendChild(time);
    tr.appendChild(td);
    td = document.createElement('td');
    let movie = document.createElement('a');
    movie.setAttribute('href', showtime.movieLink);
    movie.innerText = showtime.title;
    td.appendChild(movie);
    tr.appendChild(td);
    td = document.createElement('td');
    let theatre = document.createElement('a');
    theatre.setAttribute('href', showtime.theatreLink);
    theatre.innerText = showtime.theatre;
    td.appendChild(theatre);
    tr.appendChild(td);
    return tr;
}
/**
 * Add showtimes given to the movie table.
 * @param showtimes List of showtimes to be added to table.
 */
function loadShowtimes(showtimes) {
    let movieTable = document.getElementById('movie-table');
    for (let showtime of showtimes) {
        movieTable.appendChild(loadShowtime(showtime));
    }
}
/*
 * Load all data first, filter later.
 */
const ALL_DATA = getData();
/*
 * Filter all data by given constraints.
 */
function filterData(theatreIDs, startTime, endTime) {
    let filterData = {};
    for (let theatre of Object.keys(ALL_DATA)) {
        // Filter for theatre
        if (theatreIDs.indexOf(theatre) >= 0) {
            filterData[theatre] = JSON.parse(JSON.stringify(ALL_DATA[theatre]));
            // Filter for time
            for (let movie of filterData[theatre]) {
                movie.times = movie.times.filter(timeObj => {
                    let time = timeToDate(timeObj.time);
                    return time >= startTime && time <= endTime;
                });
            }
        }
    }
    return filterData;
}
/**
 * Load in showtimes in chronological order.
 */
function loadData(theatresData) {
    // Remove old data
    let table = document.getElementById('movie-table');
    for (let tr of document.querySelectorAll('#movie-table > tr:not([table-headers])')) {
        table.removeChild(tr);
    }
    // Load new data
    let showtimes = [];
    for (let theatre of AMCtheatres) {
        if (theatresData[theatre.id]) {
            showtimes.push(...getShowtimes(theatre, theatresData[theatre.id]));
        }
    }
    loadShowtimes(showtimes.sort((s1, s2) => {
        return s1.sortTime - s2.sortTime;
    }));
}
/**
 * Once DOM has loaded, load in data.
 */
window.onload = () => {
    // Populate filter options.
    loadTheatreOptions();
    loadTimeOptions();
    loadData(ALL_DATA);
};
function getCheckedTheatres() {
    let checkedTheatres = [];
    for (let theatre of AMCtheatres) {
        if ((document.querySelector(`#theatre-options input[name="${theatre.name}"]`)).checked) {
            checkedTheatres.push(theatre.id);
        }
    }
    return checkedTheatres;
}
/**
 * Return a date object representing the time limit inside the given element.
 * @param timeElem div containing the time selectors.
 */
function parseTimeInput(timeElem) {
    let hourDD = timeElem.querySelector('select[name="hours"]');
    let hour = parseInt(hourDD.options[hourDD.selectedIndex].value);
    let ampmDD = timeElem.querySelector('select[name="ampm"]');
    let ampm = ampmDD.options[ampmDD.selectedIndex].value;
    if (ampm === 'pm') {
        hour += 12;
    }
    let minuteDD = timeElem.querySelector('select[name="minutes"]');
    let minute = parseInt(minuteDD.options[minuteDD.selectedIndex].value);
    let time = new Date();
    time.setHours(hour);
    time.setMinutes(minute);
    time.setSeconds(0);
    return time;
}
/**
 * Get the time options that were filled in.
 */
function getTimeConstraints() {
    let timeStartElem = document.getElementById('time-start');
    let timeEndElem = document.getElementById('time-end');
    let startTime = parseTimeInput(timeStartElem);
    let endTime = parseTimeInput(timeEndElem);
    return { start: startTime, end: endTime };
}
// Control elements of page
/**
 * Document already loaded from loaddata script.
 */
document.addEventListener('click', function (event) {
    if (!(event.target instanceof Element)) {
        console.log('Event not a target of element');
        return;
    }
    /*
     * Deal with filtering.
     */
    if (event.target.matches('#submit-filter')) {
        let checkedTheatres = getCheckedTheatres();
        let times = getTimeConstraints();
        let data = filterData(checkedTheatres, times.start, times.end);
        loadData(data);
    }
});
